typedef enum
{
    eAzureIoTErrorOutOfMemory,
    eAzureIoTErrorFailed,
    eAzureIoTSuccess
} AzureIoTResult_t;

#define AZLogError printf
#define sizeofarray(x) (sizeof(x)/sizeof(x[0]))

#define azureiotjwsRSA3072_SIZE                    384                                              /**< Size of the RSA 3072 key. */
#define azureiotjwsSHA256_SIZE                     32                                               /**< Size of the SHA256 hash. */
#define azureiotjwsSHA_CALCULATION_SCRATCH_SIZE    azureiotjwsRSA3072_SIZE + azureiotjwsSHA256_SIZE /**< Size of the sha calculation scratch space. */
#define azureiotjwsPKCS7_PAYLOAD_OFFSET    19


static void printHash(const uint8_t* hash, size_t length)
{
    for (size_t i = 0; i < length; i++)
    {
        printf("%02x", hash[i]);
        if (i < (length - 1))
        {
            printf(" ");
        }
    }
}

static int myrand(void *rng_state, unsigned char *output, size_t len)
{
#if !defined(__OpenBSD__)
    size_t i;

    if (rng_state != NULL)
        rng_state = NULL;

    for (i = 0; i < len; ++i)
        output[i] = rand();
#else
    if (rng_state != NULL)
        rng_state = NULL;

    arc4random_buf(output, len);
#endif /* !OpenBSD */

    return (0);
}

static AzureIoTResult_t prvJWS_SHA256Calculate( const uint8_t * pucInput,
                                                uint32_t ulInputLength,
                                                uint8_t * pucOutput )
{
    mbedtls_md_context_t ctx;
    mbedtls_md_type_t md_type = MBEDTLS_MD_SHA256;

    mbedtls_md_init( &ctx );
    mbedtls_md_setup( &ctx, mbedtls_md_info_from_type( md_type ), 0 );
    mbedtls_md_starts( &ctx );
    mbedtls_md_update( &ctx, pucInput, ulInputLength );
    mbedtls_md_finish( &ctx, pucOutput );
    mbedtls_md_free( &ctx );

    return eAzureIoTSuccess;
}

static AzureIoTResult_t prvJWS_RS256Verify( uint8_t * pucInput,
                                            uint32_t ulInputLength,
                                            uint8_t * pucSignature,
                                            uint32_t ulSignatureLength,
                                            uint8_t * pucN,
                                            uint32_t ulNLength,
                                            uint8_t * pucE,
                                            uint32_t ulELength,
                                            uint8_t * pucBuffer,
                                            uint32_t ulBufferLength )
{
    AzureIoTResult_t xResult;
    int32_t lMbedTLSResult;
    uint8_t * pucShaBuffer;
    size_t ulDecryptedLength;
    mbedtls_rsa_context ctx;
    int shaMatchResult;

    if( ulBufferLength < azureiotjwsSHA_CALCULATION_SCRATCH_SIZE )
    {
        AZLogError( "[JWS] Buffer Not Large Enough" );
        return eAzureIoTErrorOutOfMemory;
    }

    pucShaBuffer = pucBuffer + azureiotjwsRSA3072_SIZE;

    /* The signature is encrypted using the input key. We need to decrypt the */
    /* signature which gives us the SHA256 inside a PKCS7 structure. We then compare */
    /* that to the SHA256 of the input. */
#if MBEDTLS_VERSION_NUMBER >= 0x03000000
    mbedtls_rsa_init( &ctx );
#else
    mbedtls_rsa_init( &ctx, MBEDTLS_RSA_PKCS_V15, 0 );
#endif

    lMbedTLSResult = mbedtls_rsa_import_raw( &ctx,
                                             pucN, ulNLength,
                                             NULL, 0,
                                             NULL, 0,
                                             NULL, 0,
                                             pucE, ulELength );

    if( lMbedTLSResult != 0 )
    {
        AZLogError( "[JWS] mbedtls_rsa_import_raw res: %i", ((int)lMbedTLSResult) );
        mbedtls_rsa_free( &ctx );
        return eAzureIoTErrorFailed;
    }

    lMbedTLSResult = mbedtls_rsa_complete( &ctx );

    if( lMbedTLSResult != 0 )
    {
        AZLogError( "[JWS] mbedtls_rsa_complete res: %i", (int) lMbedTLSResult );
        mbedtls_rsa_free( &ctx );
        return eAzureIoTErrorFailed;
    }

    lMbedTLSResult = mbedtls_rsa_check_pubkey( &ctx );

    if( lMbedTLSResult != 0 )
    {
        AZLogError( "[JWS] mbedtls_rsa_check_pubkey res: %i", (int)lMbedTLSResult );
        mbedtls_rsa_free( &ctx );
        return eAzureIoTErrorFailed;
    }

    /* RSA */
#if MBEDTLS_VERSION_NUMBER >= 0x03000000
goto SKIP_DECRYPT;
    lMbedTLSResult = mbedtls_rsa_pkcs1_decrypt( &ctx, NULL, NULL, &ulDecryptedLength, pucSignature, pucBuffer, azureiotjwsRSA3072_SIZE );
#else
    lMbedTLSResult = mbedtls_rsa_pkcs1_decrypt( &ctx, NULL, NULL, MBEDTLS_RSA_PUBLIC, &ulDecryptedLength, pucSignature, pucBuffer, azureiotjwsRSA3072_SIZE );
#endif

    if( lMbedTLSResult != 0 )
    {
        AZLogError( "[JWS] mbedtls_rsa_pkcs1_decrypt res: %i", (int)lMbedTLSResult );
        mbedtls_rsa_free( &ctx );
        return eAzureIoTErrorFailed;
    }
SKIP_DECRYPT:
    // mbedtls_rsa_free( &ctx );

    xResult = prvJWS_SHA256Calculate( pucInput, ulInputLength,
                                      pucShaBuffer );

    if( xResult != eAzureIoTSuccess )
    {
        AZLogError( "[JWS] prvJWS_SHA256Calculate failed" );
        return xResult;
    }

    printf( ">>> pucInput (size=%d)\r\n", ulInputLength );
    printHash( pucInput, ulInputLength );
    printf("\r\n");

    printf( ">>> pucSignature (size=%d)\r\n", ulSignatureLength );
    printHash( pucSignature, ulSignatureLength );
    printf("\r\n");

    printf( ">>> pucE (size=%d)\r\n", ulELength );
    printHash( pucE, ulELength );
    printf("\r\n");

    printf( ">>> pucN (size=%d)\r\n", ulNLength );
    printHash( pucN, ulNLength );
    printf("\r\n");

    printf(">>> pucShaBuffer (size=%d)\r\n", azureiotjwsSHA256_SIZE);
    printHash( pucShaBuffer, azureiotjwsSHA256_SIZE );
    printf("\r\n");

    printf(">>> pucBuffer (size=%d)\r\n", ulBufferLength);
    printHash( pucBuffer + azureiotjwsPKCS7_PAYLOAD_OFFSET, azureiotjwsSHA256_SIZE );
    printf("\r\n");

    /* TODO: remove this once we have a valid PKCS7 parser. */
    shaMatchResult = memcmp( pucBuffer + azureiotjwsPKCS7_PAYLOAD_OFFSET, pucShaBuffer, azureiotjwsSHA256_SIZE );

    if( shaMatchResult != 0 )
    {
        AZLogError( "[JWS] SHA of JWK does NOT match" );
        xResult = eAzureIoTErrorFailed;
    }
    else
    {
        xResult = eAzureIoTSuccess;
    }

    // unsigned char output[1000];
    // lMbedTLSResult = mbedtls_rsa_pkcs1_encrypt(&ctx, myrand, NULL, MBEDTLS_RSA_PUBLIC, azureiotjwsSHA256_SIZE, pucShaBuffer, output);

#if MBEDTLS_VERSION_NUMBER >= 0x03000000
    lMbedTLSResult = mbedtls_rsa_pkcs1_verify(&ctx, MBEDTLS_MD_SHA256, azureiotjwsSHA256_SIZE, pucShaBuffer, pucSignature);
#else
    // lMbedTLSResult = mbedtls_rsa_pkcs1_verify(&ctx, NULL, NULL, MBEDTLS_RSA_PUBLIC, MBEDTLS_MD_SHA256, azureiotjwsSHA256_SIZE, pucShaBuffer, pucInput);
    lMbedTLSResult = mbedtls_rsa_pkcs1_verify(&ctx, NULL, NULL, MBEDTLS_RSA_PUBLIC, MBEDTLS_MD_SHA256, azureiotjwsSHA256_SIZE, pucShaBuffer, pucSignature);
#endif

    if (lMbedTLSResult != 0)
    {
        printf("lMbedTLSResult failed (%0x08) \r\n", -1 * lMbedTLSResult);
        return lMbedTLSResult;
    }
    else
    {
        printf("mbedtls_rsa_pkcs1_verify succeeded!\r\n");
    }

    // printf(">>> output (size=%d)\r\n", azureiotjwsRSA3072_SIZE);
    // printHash( output, azureiotjwsRSA3072_SIZE );
    // printf("\r\n");

    mbedtls_rsa_free( &ctx );

    return xResult;
}

